import React,{useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux';
import {addToCart} from '../actions/cartAction';
import {
    Row,
    Col,
    Form,
    Button,
    Card,
    Image,
    ListGroup,
    ListGroupItem
} from 'react-bootstrap';
import Message from '../components/Message';
import {Link} from 'react-router-dom';

function CartPage({match, location, history}) {
    const productId = match.params.id;
    const qnt=location.search ? Number(location.search.split('=')[1]):1;
    const dispatch = useDispatch()
    useEffect(()=>{
        if(productId)
        {
            dispatch(addToCart(productId, qnt));
        }
    },[dispatch,productId,qnt])
    const cart = useSelector(state=>state.cart)
    const {cartItems} = cart;
    console.log(cartItems);
    return (
        <>
        <Row>
            <Col md={8}>
                <h1>Shopping Cart</h1>
                {
                    cartItems.length === 0? (
                        <Message>Your Cart is Empty !
                            <Link to="/">Go Back</Link>
                        </Message>
                    ) : (
                        <ListGroup variant="flush">
                            {
                                cartItems.map(item=>{
                                    <ListGroupItem>
                                        <Row>
                                            <Col md={2}>
                                                <Image src={item.image} alt={item.name} fluid rounded></Image>
                                            </Col>
                                            <Col md={3}>
                                                <Link to={`api/products/${item.product}`}>{item.name}</Link>
                                            </Col>
                                            <Col md={2}>
                                                ${item.price}
                                            </Col>
                                            <Col md={2}>
                                                {/* <Form.Control as= "select" value={qnt} onChange={(e)=>setQnt(e.target.value)}>
                                                    {
                                                        [...Array(product.countInStock).keys()].map((x)=>(
                                                            <option key={x+1} value={x+1}>
                                                            {x+1}
                                                            </option>
                                                        ))
                                                    }
                                                </Form.Control> */}
                                            </Col>
                                        </Row>
                                    </ListGroupItem>
                                })
                            }
                        </ListGroup>
                    )
                }
            </Col>
            <Col md={4}></Col>
        </Row>
        </>
    )
}

export default CartPage
